{
	"name" : "{{DataSourceName}}",
    "description": "The data source for he Youtube Channel Video Search",
    "type": "azureblob",
    "credentials": {
        "connectionString": "{{AzureStorageConnectionString}}"
    },
    "container": {
        "name": "{{AzureStorageContainerName}}"
    }
}

{
    "name": "{{SkillsetName}}",
    "description": "The skillset for the indexer used in the Youtube Channel Video Search",
    "skills": [
        {
            "@odata.type": "#Microsoft.Skills.Text.KeyPhraseExtractionSkill",
            "defaultLanguageCode": "en",
            "context": "/document",
            "inputs": [
                {
                    "name": "text",
                    "source": "/document/snippet/description"
                }
            ],
            "outputs": [
                {
                    "name": "keyPhrases",
                    "targetName": "keyPhrases"
                }
            ]
        },
        {
            "@odata.type": "#Microsoft.Skills.Text.EntityRecognitionSkill",
            "defaultLanguageCode": "en",
            "context": "/document",
            "categories": [
                "Person",
                "Location",
                "Organization"
            ],
            "inputs": [
                {
                    "name": "text",
                    "source": "/document/snippet/description"
                }
            ],
            "outputs": [
                {
                    "name": "persons",
                    "targetName": "persons"
                },
                {
                    "name": "locations",
                    "targetName": "locations"
                },
                {
                    "name": "organizations",
                    "targetName": "organizations"
                },
                {
                    "name": "entities",
                    "targetName": "entities"
                }
            ]
        }
    ],
    "cognitiveServices": {
        "@odata.type": "#Microsoft.Azure.Search.CognitiveServicesByKey",
        "key": "{{CognitiveServicesKey}}"
    }
}

{
	"name": "{{SkillsetName}}",
    "description": "The skillset for the indexer used in the Youtube Channel Video Search",
    "skills": [
        {
            "@odata.type": "#Microsoft.Skills.Text.KeyPhraseExtractionSkill",
            "inputs": [
                {
                    "name": "text",
                    "source": "/document/snippet/description"
                }
            ],
            "outputs": [
                {
                    "name": "keyPhrases",
                    "targetName": "myKeyPhrases"
                }
            ]
        },
        {
            "@odata.type": "#Microsoft.Skills.Text.EntityRecognitionSkill",
            "context": "/document",
            "categories": [
                "Person",
                "Location",
                "Organization"
            ],
            "defaultLanguageCode": "en",
            "inputs": [
                {
                    "name": "text",
                    "source": "/document/snippet/description"
                }
            ],
            "outputs": [
                {
                    "name": "persons",
                    "targetName": "persons"
                },
                {
                    "name": "locations",
                    "targetName": "locations"
                },
                {
                    "name": "organizations",
                    "targetName": "organizations"
                }
            ]
        }
    ],
    "cognitiveServices": {
        "@odata.type": "#Microsoft.Azure.Search.CognitiveServicesByKey",
        "key": "{{CognitiveServicesKey}}"
    }
}

{
    "name": "{{IndexerName}}",
    "description": "a cool indexer",
    "dataSourceName": "{{DataSourceName}}",
    "targetIndexName": "{{IndexName}}",
    "skillsetName":"{{SkillsetName}}",
    "parameters": {
        "configuration": {
            "parsingMode": "jsonArray"
        }
    },
    "outputFieldMappings": [
        {
            "sourceFieldName": "/document/organizations",
            "targetFieldName": "organizations",
            "mappingFunction": null
        },
        {
            "sourceFieldName": "/document/locations",
            "targetFieldName": "locations",
            "mappingFunction": null
        },
        {
            "sourceFieldName": "/document/keyPhrases",
            "targetFieldName": "keyphrases",
            "mappingFunction": null
        },
        {
            "sourceFieldName": "/document/entities",
            "targetFieldName": "entities",
            "mappingFunction": null
        }
    ]
}
